package com.vmware.dcm.compiler;

import com.vmware.dcm.ViewsWithChecks;

import javax.annotation.concurrent.NotThreadSafe;
import java.util.LinkedHashMap;
import java.util.Map;

/**
 * Used to track the set of symbol references from the constraint-views.
 */
@NotThreadSafe
class ReferencedSymbols {
    // From the AST generated by parsing SQL
    private final Map<String, ViewsWithChecks> nonConstraintViews;
    private final Map<String, ViewsWithChecks> constraintViews;
    private final Map<String, ViewsWithChecks> objectiveFunctionViews;

    ReferencedSymbols() {
        nonConstraintViews = new LinkedHashMap<>();
        constraintViews = new LinkedHashMap<>();
        objectiveFunctionViews = new LinkedHashMap<>();
    }

    Map<String, ViewsWithChecks> getNonConstraintViews() {
        return nonConstraintViews;
    }

    Map<String, ViewsWithChecks> getObjectiveFunctionViews() {
        return objectiveFunctionViews;
    }

    Map<String, ViewsWithChecks> getConstraintViews() {
        return constraintViews;
    }

    void printKeys() {
        System.out.println("----------");
        System.out.println("NonConstraintViews: " + nonConstraintViews.keySet());
        System.out.println("ConstraintViews: " + constraintViews.keySet());
        System.out.println("----------");
    }
}